};

// Task Configuration for Blueprint task management
const TASK_CONFIG = {
  TASK_TIMEOUT_DEFAULT: 600000, // 10 minutes
  TASK_TIMEOUT_FILE_PROCESSING: 1800000, // 30 minutes
  TASK_TIMEOUT_PLAYLIST_DOWNLOAD: 3600000, // 1 hour
  TASK_TIMEOUT_WEB_SCRAPING: 1200000, // 20 minutes
  TASK_TIMEOUT_ACADEMIC_SEARCH: 300000, // 5 minutes
  TASK_TIMEOUT_PDF_PROCESSING: 900000, // 15 minutes

  TASK_PROGRESS_UPDATE_INTERVAL: 500, // 500ms for smoother updates
  TASK_STATUS_CHECK_INTERVAL: 2000, // 2 seconds
  TASK_MAX_RETRIES: 5, // Increased for production reliability
  TASK_RETRY_DELAY: 3000,
  TASK_CLEANUP_DELAY: 120000, // 2 minutes after completion
  TASK_HISTORY_MAX_SIZE: 200, // Increased for better tracking
  TASK_HISTORY_PERSIST: true,

  // Blueprint-specific task priorities
  TASK_PRIORITIES: {
    file_processor: 'high',
    playlist_downloader: 'medium',
    web_scraper: 'medium',
    academic_search: 'high',  // Elevated to high priority
    pdf_processor: 'high',
    management: 'critical'
  },

  // Academic Search Configuration - Complete source integration
  ACADEMIC_SEARCH: {
    // Supported academic sources
    SEARCH_SOURCES: ['arxiv', 'semantic_scholar', 'openalex', 'pubmed', 'ieee', 'acm'],
    DEFAULT_SOURCE: 'all',
    MAX_RESULTS: 100,  // Increased for production
    DEFAULT_LIMIT: 20,
    RESULTS_PER_PAGE: 10,

    // Search operations
    SUPPORTED_OPERATIONS: ['search', 'details', 'download', 'citations', 'recommendations', 'multi-source'],

    // Search types
    SEARCH_TYPES: {
      BASIC: 'basic',
      ADVANCED: 'advanced',
      CITATION: 'citation',
      AUTHOR: 'author',
      TITLE: 'title'
    },

    // Result filtering
    FILTERS: {
      DATE_RANGES: ['all', 'last_year', 'last_5_years', 'last_10_years', 'custom'],
      PUBLICATION_TYPES: ['all', 'journal', 'conference', 'preprint', 'book', 'thesis'],
      SORT_OPTIONS: ['relevance', 'date', 'citations', 'title', 'author']
