  enablePerformanceMonitoring: BASE_CONFIG.DEVELOPMENT,
  enableMemoryTracking: BASE_CONFIG.DEVELOPMENT,
  showDetailedErrors: BASE_CONFIG.DEBUG_MODE,
  enableDiagnostics: true
};

// Export the frozen constants
export { CONSTANTS };

// Also export individual configurations for convenience
export {
  ENV,
  BASE_CONFIG,
  API_CONFIG,
  SOCKET_CONFIG,
  TASK_CONFIG,
  FILE_CONFIG,
  UI_CONFIG,
  STORAGE_CONFIG,
  PERFORMANCE_CONFIG,
  FEATURE_FLAGS,
  VALIDATION_CONFIG,
  ERROR_MESSAGES,
  SUCCESS_MESSAGES,
  KEYBOARD_SHORTCUTS,
